mapper.select:
SELECT emp_no 
FROM mtgr_mng_ldgr
Where mml_no=#{mmlNo}

mapper.insert:

	SELECT NVL(SUBSTR(MAX(MML_NO),1,3) || TRIM(TO_CHAR(SUBSTR(MAX(MML_NO),4) + 1,'000')),'MML001')
	FROM   MTGR_MNG_LDGR

INSERT INTO MTGR_MNG_LDGR
(
MML_NO
, MML_RSVT_YN
, MML_USE_PRPS
, MML_RSVT_STRDT
, MML_RSVT_ENDDT
, MTGR_NO
, EMP_NO
)
VALUES
(
#{mmlNo}
,#{mmlRsvtYN}
,#{mmlUsePrps}
,#{mmlRsvtStrdt}
,#{mmlRsvtEnddt}
,#{mtgrNo}
,#{empNo}
)

mapper.delete:
DELETE FROM MTGR_MNG_LDGR
WHERE 
	MML_NO=#{mmlNo}
AND
	EMP_NO=#{empNo}
